import React, { useEffect, useState } from "react";
import { getAllTasks, addTask, deleteTask, renameTask } from "./api";

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTaskName, setNewTaskName] = useState("");
  const [newDeadline, setNewDeadline] = useState("");
  const [renameText, setRenameText] = useState({});

  const loadTasks = () => {
    getAllTasks().then(setTasks);
  };

  useEffect(() => {
    loadTasks();
  }, []);

  const handleAdd = () => {
    if (newTaskName.trim()) {
      const isoDeadline = newDeadline
        ? new Date(newDeadline).toISOString()
        : null;

      addTask({ taskName: newTaskName, deadline: isoDeadline }).then(() => {
        setNewTaskName("");
        setNewDeadline("");
        loadTasks();
      });
    }
  };

  const handleRename = (id) => {
    if (renameText[id]?.trim()) {
      renameTask(id, renameText[id]).then(loadTasks);
    }
  };

  const handleDelete = (id) => {
    deleteTask(id).then(loadTasks);
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>DevTasker</h1>

      <div style={{ marginBottom: "1rem" }}>
        <input
          type="text"
          placeholder="New task name"
          value={newTaskName}
          onChange={(e) => setNewTaskName(e.target.value)}
          style={{ marginRight: "0.5rem", padding: "0.5rem" }}
        />
        <input
          type="date"
          value={newDeadline}
          onChange={(e) => setNewDeadline(e.target.value)}
          style={{ marginRight: "0.5rem", padding: "0.5rem" }}
        />
        <button onClick={handleAdd} style={{ padding: "0.5rem 1rem", borderRadius: "6px" }}>
          Add Task
        </button>
      </div>

      <ul style={{ listStyle: "none", paddingLeft: 0 }}>
        {tasks.map((task) => (
          <li
            key={task.id}
            style={{
              marginBottom: "1rem",
              background: "#f4f4f4",
              padding: "1rem",
              borderRadius: "10px",
            }}
          >
            <div>
              <strong>{task.taskName}</strong>
              {task.deadline && (
                <span style={{ marginLeft: "0.5rem", color: "#888" }}>
                  (Due: {new Date(task.deadline).toLocaleDateString()})
                </span>
              )}
            </div>

            <input
              style={{ marginTop: "0.5rem", marginRight: "0.5rem", padding: "0.4rem" }}
              placeholder="New name"
              value={renameText[task.id] || ""}
              onChange={(e) =>
                setRenameText({ ...renameText, [task.id]: e.target.value })
              }
            />
            <button onClick={() => handleRename(task.id)} style={{ marginRight: "0.5rem", borderRadius: "6px" }}>
              Rename
            </button>
            <button onClick={() => handleDelete(task.id)} style={{ borderRadius: "6px" }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
