import React, { useEffect, useState } from "react";
import { getAllTasks, addTask, deleteTask, renameTask } from "./api";

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTaskName, setNewTaskName] = useState("");
  const [newDeadline, setNewDeadline] = useState("");
  const [renameText, setRenameText] = useState({});

  const loadTasks = () => {
    getAllTasks().then(setTasks);
  };

  useEffect(() => {
    loadTasks();
  }, []);

  const handleAdd = () => {
    if (newTaskName.trim()) {
      addTask({ taskName: newTaskName, deadline: newDeadline }).then(() => {
        setNewTaskName("");
        setNewDeadline("");
        loadTasks();
      });
    }
  };

  const handleRename = (id) => {
    if (renameText[id]?.trim()) {
      renameTask(id, renameText[id]).then(loadTasks);
    }
  };

  const handleDelete = (id) => {
    deleteTask(id).then(loadTasks);
  };

  return (
    <div style={{ padding: "1rem" }}>
      <h1>DevTasker</h1>

      <input
        type="text"
        placeholder="New task name"
        value={newTaskName}
        onChange={(e) => setNewTaskName(e.target.value)}
      />
      <input
        type="date"
        value={newDeadline}
        onChange={(e) => setNewDeadline(e.target.value)}
      />
      <button onClick={handleAdd}>Add Task</button>

      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <strong>{task.taskName}</strong>
            {task.deadline && <span> (Due: {task.deadline})</span>}

            <input
              style={{ marginLeft: "1rem" }}
              placeholder="New name"
              value={renameText[task.id] || ""}
              onChange={(e) =>
                setRenameText({ ...renameText, [task.id]: e.target.value })
              }
            />
            <button onClick={() => handleRename(task.id)}>Rename</button>
            <button onClick={() => handleDelete(task.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
